// src/components/Layout/Header.js
import React from 'react';
import { motion } from 'framer-motion';
import { Bell, Search, LogOut, User } from 'lucide-react';
import { useAuth } from '../../contexts/AuthContext';
import ThemeMenu from './ThemeMenu';

const Header = () => {
    const { user, logout } = useAuth();
    const tieneNotificaciones = false; // cambia a tu lógica real

    return (
        <motion.header
            className="bg-[var(--surface)] border-b border-[var(--border)] px-6 py-4"
            initial={{ y: -20, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ duration: 0.3 }}
        >
            <div className="flex items-center justify-between">
                {/* Buscador */}
                <div className="hidden md:flex items-center gap-3 bg-gray-50 rounded-xl px-4 py-2 w-96">
                    <Search className="w-5 h-5 text-gray-400" />
                    <input
                        type="text"
                        placeholder="Buscar en el sistema..."
                        className="bg-transparent border-none outline-none flex-1 text-[var(--text)] placeholder-gray-400"
                    />
                </div>

                {/* Acciones de usuario */}
                <div className="flex items-center gap-2 sm:gap-3">
                    {/* BOTÓN DE TEMA */}
                    <ThemeMenu />

                    {/* Notificaciones */}
                    <motion.button
                        className="relative p-2 rounded-lg hover:bg-gray-100 transition-colors"
                        whileHover={{ scale: 1.05 }}
                        whileTap={{ scale: 0.95 }}
                        transition={{ duration: 0.2 }}
                        aria-label="Notificaciones"
                      >
                        <Bell className="w-6 h-6 text-gray-600" />
                        {tieneNotificaciones && (
                          <span className="absolute -top-1 -right-1 w-3 h-3 bg-red-500 rounded-full" />
                        )}
                    </motion.button>

                    {/* Usuario + Logout */}
                    <div className="flex items-center gap-3">
                        <div className="hidden sm:block text-right">
                            <p className="text-sm font-medium text-[var(--text)]">{user?.name}</p>
                            <p className="text-xs text-[var(--muted)] capitalize">{user?.role}</p>
                        </div>

                        <div className="flex items-center gap-2">
                            <motion.button
                                className="w-10 h-10 rounded-full flex items-center justify-center text-white font-medium bg-gradient-to-br from-[var(--primary)] to-[var(--secondary)]"
                                whileHover={{ scale: 1.05 }}
                                whileTap={{ scale: 0.95 }}
                                transition={{ duration: 0.2 }}
                                aria-label="Perfil"
                            >
                                <User className="w-5 h-5" />
                            </motion.button>

                            <motion.button
                                onClick={logout}
                                className="p-2 rounded-lg hover:bg-red-50 text-red-600 transition-colors"
                                whileHover={{ scale: 1.05 }}
                                whileTap={{ scale: 0.95 }}
                                transition={{ duration: 0.2 }}
                                title="Cerrar Sesión"
                            >
                                <LogOut className="w-5 h-5" />
                            </motion.button>
                        </div>
                    </div>
                </div>
            </div>
        </motion.header>
    );
};

export default Header;
