import React, { createContext, useState, useEffect } from 'react';

// Crea el contexto del tema
export const ThemeContext = createContext();

// Crea un proveedor del tema
export const ThemeProvider = ({ children }) => {
    // Obtiene el tema guardado en el localStorage o usa 'light' por defecto
    const [theme, setTheme] = useState(localStorage.getItem('theme') || 'light');

    // Actualiza el tema en el localStorage cada vez que cambia
    useEffect(() => {
        localStorage.setItem('theme', theme);
        document.documentElement.setAttribute('data-theme', theme); // Actualiza el atributo data-theme
    }, [theme]);

    // Funci贸n para cambiar el tema
    const toggleTheme = () => {
        setTheme(theme === 'light' ? 'dark' : 'light');
    };

    // Devuelve el proveedor del contexto con el valor del tema y la funci贸n para cambiarlo
    return (
        <ThemeContext.Provider value={{ theme, toggleTheme }}>
            {children}
        </ThemeContext.Provider>
    );
};
