import React, { useState, useEffect } from 'react';
import { Outlet } from 'react-router-dom';
import Sidebar from './Sidebar';
import Header from './Header';

const Layout = () => {
    const [sidebarOpen, setSidebarOpen] = useState(false);
    const [isDesktop, setIsDesktop] = useState(window.innerWidth >= 1024);
    const [collapsed, setCollapsed] = useState(false); // controla el modo contraído/expandido

    useEffect(() => {
        const handleResize = () => {
            setIsDesktop(window.innerWidth >= 1024);
            if (window.innerWidth >= 1024) {
                setSidebarOpen(true); // siempre visible en desktop
            } else {
                setSidebarOpen(false); // oculto en mobile por defecto
            }
        };

        window.addEventListener('resize', handleResize);
        handleResize(); // inicializar al cargar

        return () => window.removeEventListener('resize', handleResize);
    }, []);

    const toggleSidebar = () => {
        if (!isDesktop) {
            setSidebarOpen(!sidebarOpen);
        }
    };

    const closeSidebar = () => {
        if (!isDesktop) {
            setSidebarOpen(false);
        }
    };

    return (
        <div className="flex h-screen bg-gray-50">
            {/* Sidebar siempre visible en desktop */}
            <Sidebar isOpen={isDesktop ? true : sidebarOpen} onClose={closeSidebar} />

            <div className="flex-1 flex flex-col overflow-hidden">
                <Header onMenuToggle={toggleSidebar} />
                <main className="flex-1 overflow-y-auto p-6">
                    <Outlet />
                </main>
            </div>
        </div>
    );
};

export default Layout;
