// src/components/Layout/Header.js
import React from 'react';
import { motion } from 'framer-motion';
import { Bell, Search, LogOut, User } from 'lucide-react';
import { useAuth } from '../../contexts/AuthContext';
import ThemeMenu from './ThemeMenu';

const Header = () => {
    const { user, logout } = useAuth();
    const tieneNotificaciones = false; // cambia a tu lógica real

    return (
        <motion.header
            className="px-6 py-4"
            initial={{ y: -20, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ duration: 0.3 }}
            style={{
              background: "var(--surface)",
              borderBottom: `1px solid var(--border)`
            }}
        >
            <div className="flex items-center justify-between">
                {/* Buscador */}
                <div className="hidden md:flex items-center gap-3 rounded-xl px-4 py-2 w-96"
                     style={{ background: "var(--input-bg)" }}>
                    <Search className="w-5 h-5" style={{ color: "var(--muted)" }} />
                    <input
                        type="text"
                        placeholder="Buscar en el sistema..."
                        className="bg-transparent border-none outline-none flex-1"
                        style={{ color: "var(--text)" , caretColor: "var(--primary)"}}
                    />
                </div>

                {/* Acciones de usuario */}
                <div className="flex items-center gap-2 sm:gap-3">
                    {/* BOTÓN DE TEMA */}
                    <ThemeMenu />

                    {/* Notificaciones */}
                    <motion.button
                        className="p-2 rounded-lg transition-colors"
                        whileHover={{ scale: 1.05 }}
                        whileTap={{ scale: 0.95 }}
                        transition={{ duration: 0.2 }}
                        aria-label="Notificaciones"
                        style={{ background: "transparent" }}
                      >
                        <Bell className="w-6 h-6" style={{ color: "var(--text)" }} />
                        {tieneNotificaciones && (
                          <span style={{
                            position: 'absolute',
                            marginLeft: '0.75rem',
                            marginTop: '-0.25rem',
                            width: 12,
                            height: 12,
                            background: '#ef4444',
                            borderRadius: 9999
                          }} />
                        )}
                    </motion.button>

                    {/* Usuario + Logout */}
                    <div className="flex items-center gap-3">
                        <div className="hidden sm:block text-right">
                            <p className="text-sm font-medium" style={{ color: "var(--text)" }}>{user?.name}</p>
                            <p className="text-xs" style={{ color: "var(--muted)", textTransform: 'capitalize' }}>{user?.role}</p>
                        </div>

                        <div className="flex items-center gap-2">
                            <motion.button
                                className="w-10 h-10 rounded-full flex items-center justify-center text-white font-medium"
                                whileHover={{ scale: 1.05 }}
                                whileTap={{ scale: 0.95 }}
                                transition={{ duration: 0.2 }}
                                aria-label="Perfil"
                                style={{ background: "linear-gradient(135deg, var(--primary) 0%, var(--secondary) 100%)" }}
                            >
                                <User className="w-5 h-5" />
                            </motion.button>

                            <motion.button
                                onClick={logout}
                                className="p-2 rounded-lg transition-colors"
                                whileHover={{ scale: 1.05 }}
                                whileTap={{ scale: 0.95 }}
                                transition={{ duration: 0.2 }}
                                title="Cerrar Sesión"
                                style={{ color: "#ef4444", background: "transparent" }}
                            >
                                <LogOut className="w-5 h-5" />
                            </motion.button>
                        </div>
                    </div>
                </div>
            </div>
        </motion.header>
    );
};

export default Header;
