import React, { useContext } from 'react';
import { Moon, Sun } from 'lucide-react';
import { ThemeContext } from './ThemeContext';

const ThemeToggle = () => {
  const { theme, toggleTheme } = useContext(ThemeContext);

  return (
    <button
      onClick={toggleTheme}
      className="w-full py-2 px-4 rounded-xl transition-colors flex items-center justify-center gap-2 hover:bg-gray-100 dark:hover:bg-gray-700"
    >
      {theme === 'light' ? (
        <>
          <Moon className="w-5 h-5 text-gray-600" /> Modo Oscuro
        </>
      ) : (
        <>
          <Sun className="w-5 h-5 text-yellow-500" /> Modo Claro
        </>
      )}
    </button>
  );
};

export default ThemeToggle;
