// Filtra SOLO el módulo actual en base a la ruta
const getCurrentModule = () => {
  const currentPath = location.pathname;

  // Buscar si el path pertenece a un módulo con hijos
  const parentModule = menuItems.find(item =>
    item.children?.some(child => currentPath.startsWith(child.path))
  );

  if (parentModule) return [parentModule];

  // Si es un módulo sin hijos, lo buscamos por path
  const singleModule = menuItems.find(item => currentPath.startsWith(item.path));
  if (singleModule) {
    // Si no tiene hijos → le agregamos opciones dinámicas
    if (!singleModule.children) {
      singleModule.children = [
        { title: "Opción 1", path: `${singleModule.path}/opcion1`, icon: Settings },
        { title: "Opción 2", path: `${singleModule.path}/opcion2`, icon: Settings },
        { title: "Opción 3", path: `${singleModule.path}/opcion3`, icon: Settings },
      ];
    }
    return [singleModule];
  }

  return []; // si no encuentra nada
};

const filteredMenuItems = getCurrentModule();
