import React, { useState, useRef, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import {
  Users, Calendar, DollarSign, ShoppingCart,
  Package, Settings, BarChart3, Shield, Church,
  ChevronDown, ChevronRight, FileText, Clock,
  TrendingUp, Archive, LayoutGrid
} from 'lucide-react';
import { useAuth } from '../../contexts/AuthContext';

const Sidebar = ({ collapsed, toggleCollapse }) => {
  const location = useLocation();
  const { user, hasPermission } = useAuth();
  const [expandedMenus, setExpandedMenus] = useState({});
  const [openFlyoutMenu, setOpenFlyoutMenu] = useState(null);
  const [flyoutPositionTop, setFlyoutPositionTop] = useState(0);
  const flyoutRef = useRef(null);

  // Lista de men√∫s con permisos y rutas
  const menuItems = [
    { id: 'personal', title: 'M√≥dulo Personal', icon: Users, path: '/personal', permission: 'personal' },
    {
      id: 'liturgical',
      title: 'Actos Lit√∫rgicos',
      icon: Church,
      permission: 'liturgical',
      children: [
        { title: 'Gestionar Actos', path: '/liturgical/manage', icon: Church },
        { title: 'Horarios', path: '/liturgical/schedules', icon: Clock },
        { title: 'Reservas', path: '/liturgical/reservations', icon: Calendar },
        { title: 'Reportes', path: '/liturgical/reports', icon: FileText }
      ]
    },
    { id: 'sales', title: 'M√≥dulo Ventas', icon: TrendingUp, path: '/sales', permission: 'sales' },
    { id: 'purchases', title: 'M√≥dulo Compras', icon: ShoppingCart, path: '/purchases', permission: 'purchases' },
    { id: 'warehouse', title: 'M√≥dulo Almac√©n', icon: Package, path: '/warehouse', permission: 'warehouse' },
    { id: 'accounting', title: 'M√≥dulo Contabilidad', icon: DollarSign, path: '/accounting', permission: 'accounting' },
    {
      id: 'reports',
      title: 'M√≥dulo Reportes',
      icon: BarChart3,
      permission: 'reports',
      children: [
        { title: 'Gerenciales', path: '/reports/management', icon: TrendingUp },
        { title: 'Transaccionales', path: '/reports/transactions', icon: Archive }
      ]
    },
    {
      id: 'security',
      title: 'M√≥dulo Seguridad',
      icon: Shield,
      permission: 'security',
      children: [
        { title: 'Usuarios', path: '/security/users', icon: Users },
        { title: 'Roles', path: '/security/roles', icon: Shield },
        { title: 'Permisos', path: '/security/permissions', icon: Settings }
      ]
    },
    { id: 'configuration', title: 'M√≥dulo Configuraci√≥n', icon: Settings, path: '/configuration', permission: 'configuration' }
  ];

  // üîë Determinar el m√≥dulo actual en base a la ruta
  const getCurrentModule = () => {
    const currentPath = location.pathname;

    // Buscar si el path pertenece a un m√≥dulo con hijos
    const parentModule = menuItems.find(item =>
      item.children?.some(child => currentPath.startsWith(child.path))
    );

    if (parentModule) return [parentModule];

    // Si es un m√≥dulo sin hijos, lo buscamos por path
    const singleModule = menuItems.find(item => currentPath.startsWith(item.path));
    if (singleModule) {
      // Si no tiene hijos ‚Üí le agregamos opciones din√°micas
      if (!singleModule.children) {
        singleModule.children = [
          { title: "Opci√≥n 1", path: `${singleModule.path}/opcion1`, icon: Settings },
          { title: "Opci√≥n 2", path: `${singleModule.path}/opcion2`, icon: Settings },
          { title: "Opci√≥n 3", path: `${singleModule.path}/opcion3`, icon: Settings },
        ];
      }
      return [singleModule];
    }

    return []; // si no encuentra nada
  };

  // Solo muestra el m√≥dulo actual (seg√∫n permisos)
  const filteredMenuItems = getCurrentModule().filter(item => hasPermission(item.permission));

  // Activo si coincide o comienza con la ruta
  const isActive = (path) => location.pathname.startsWith(path);

  const isParentActive = (children) => children?.some(child => isActive(child.path));

  // Listener para cerrar flyout al hacer click fuera
  useEffect(() => {
    function handleClickOutside(event) {
      if (flyoutRef.current && !flyoutRef.current.contains(event.target)) {
        setOpenFlyoutMenu(null);
      }
    }
    if (openFlyoutMenu) {
      document.addEventListener('mousedown', handleClickOutside);
    }
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [openFlyoutMenu]);

  const SubmenuInline = ({ item }) => (
    <>
      <button
        onClick={() => setExpandedMenus(prev => ({ ...prev, [item.id]: !prev[item.id] }))}
        role="button"
        className="w-full flex items-center gap-3 p-2 rounded-lg font-medium transition-all duration-200"
        style={{
          background: isParentActive(item.children) ? "var(--surface-2)" : "transparent",
          color: isParentActive(item.children) ? "var(--primary)" : "var(--text)"
        }}
        aria-expanded={!!expandedMenus[item.id]}
        aria-controls={`${item.id}-submenu`}
      >
        <item.icon className="w-5 h-5" />
        <span className="text-sm">{item.title}</span>
        {expandedMenus[item.id] ? (
          <ChevronDown className="w-4 h-4 ml-auto" />
        ) : (
          <ChevronRight className="w-4 h-4 ml-auto" />
        )}
      </button>
      {expandedMenus[item.id] && (
        <div id={`${item.id}-submenu`} className="ml-6 space-y-1 mt-1" role="region">
          {item.children.map(child => (
            <Link
              key={child.path}
              to={child.path}
              className="flex items-center gap-2 p-1 rounded text-sm"
              style={{
                background: isActive(child.path) ? "var(--surface-2)" : "transparent",
                color: isActive(child.path) ? "var(--primary)" : "var(--muted)"
              }}
            >
              <child.icon className="w-4 h-4" />
              <span>{child.title}</span>
            </Link>
          ))}
        </div>
      )}
    </>
  );

  const FlyoutMenu = ({ item }) => (
    <div
      ref={flyoutRef}
      className="fixed left-[80px] rounded-lg shadow-lg z-50 border"
      style={{ 
        top: flyoutPositionTop, 
        minWidth: 192,
        background: "var(--surface)",
        borderColor: "var(--border)"
      }}
      role="menu"
    >
      {item.children.map(child => (
        <Link
          key={child.path}
          to={child.path}
          className="flex items-center gap-2 p-2 text-sm rounded"
          style={{
            background: isActive(child.path) ? "var(--surface-2)" : "transparent",
            color: isActive(child.path) ? "var(--primary)" : "var(--text)"
          }}
          onClick={() => setOpenFlyoutMenu(null)}
        >
          <child.icon className="w-4 h-4" />
          <span>{child.title}</span>
        </Link>
      ))}
    </div>
  );

  return (
    <aside
      className="h-full flex flex-col shrink-0 relative"
      style={{ 
        width: collapsed ? 80 : 256,
        background: "var(--surface)",
        borderRight: `1px solid var(--border)`
      }}
    >
      {/* Logo */}
      <Link
        to="/dashboard"
        className={`flex items-center gap-3 px-4 py-4 border-b cursor-pointer ${collapsed ? 'justify-center' : ''}`}
        style={{ borderColor: "var(--border)" }}
      >
        <Church className="w-8 h-8" style={{ color: "var(--primary)" }} />
        {!collapsed && (
          <div>
            <h1 className="text-lg font-bold" style={{ color: "var(--text)" }}>SisParroquia</h1>
            <p className="text-xs" style={{ color: "var(--muted)" }}>Sistema de Gesti√≥n</p>
          </div>
        )}
      </Link>

      {/* Bot√≥n colapsar/expandir */}
      <div className="p-2 border-b" style={{ borderColor: "var(--border)" }}>
        <button
          onClick={toggleCollapse}
          className="w-full flex items-center justify-center p-2 rounded-lg"
          style={{ color: "var(--text)" }}
        >
          <LayoutGrid className="w-5 h-5" />
          {!collapsed && <span className="ml-2 text-sm font-medium">Men√∫</span>}
        </button>
      </div>

      {/* Men√∫ principal */}
      <nav className="flex-1 overflow-y-auto p-2 space-y-2 relative">
        {filteredMenuItems.map(item => {
          const hasChildren = !!item.children?.length;
          const activeParent = isParentActive(item.children);

          if (hasChildren) {
            if (collapsed) {
              return (
                <div key={item.id} className="relative flex justify-center">
                  <button
                    onClick={(e) => handleCollapsedMenuClick(item.id, e)}
                    className="flex items-center p-3 rounded-lg font-medium"
                    style={{
                      background: activeParent ? "var(--surface-2)" : "transparent",
                      color: activeParent ? "var(--primary)" : "var(--text)"
                    }}
                  >
                    <item.icon className="w-5 h-5" />
                  </button>
                  {openFlyoutMenu === item.id && <FlyoutMenu item={item} />}
                </div>
              );
            } else {
              return <SubmenuInline key={item.id} item={item} />;
            }
          } else {
            return (
              <Link
                key={item.id}
                to={item.path}
                className={`flex items-center p-2 rounded-lg font-medium transition-all ${collapsed ? 'justify-center' : 'gap-3'}`}
                style={{
                  background: isActive(item.path) ? "var(--surface-2)" : "transparent",
                  color: isActive(item.path) ? "var(--primary)" : "var(--text)"
                }}
              >
                <item.icon className="w-5 h-5" />
                {!collapsed && <span className="text-sm">{item.title}</span>}
              </Link>
            );
          }
        })}
      </nav>

      {/* Informaci√≥n usuario */}
      <div className="p-2 border-t" style={{ borderColor: "var(--border)" }}>
        <div className={`flex items-center gap-2 ${collapsed ? 'justify-center' : ''}`}>
          <div 
            className="w-8 h-8 rounded-full flex items-center justify-center"
            style={{
              background: "linear-gradient(135deg, var(--primary), var(--secondary))"
            }}
          >
            <span className="text-white text-sm font-bold">{user?.name?.charAt(0) || 'U'}</span>
          </div>
          {!collapsed && (
            <div>
              <p className="text-sm font-medium truncate" style={{ color: "var(--text)" }}>{user?.name || 'Usuario'}</p>
              <p className="text-xs capitalize" style={{ color: "var(--muted)" }}>{user?.role || 'usuario'}</p>
            </div>
          )}
        </div>
      </div>
    </aside>
  );
};

export default Sidebar;
