import React, { useState } from 'react';
import { Outlet } from 'react-router-dom';
import Sidebar from './Sidebar';
import Header from './Header';
import { ThemeProvider } from '../src/'; // Importa el ThemeProvider

const Layout = () => {
    const [collapsed, setCollapsed] = useState(false); // controla colapso/expansi칩n

    const toggleSidebar = () => setCollapsed(!collapsed);

    return (
        <ThemeProvider> {/* Envuelve la aplicaci칩n con el ThemeProvider */}
            <div className="flex h-screen bg-gray-50">
                {/* Sidebar siempre visible */}
                <Sidebar collapsed={collapsed} toggleCollapse={toggleSidebar} />

                {/* Contenido principal con margen din치mico */}
                <main className="flex-1 flex flex-col overflow-hidden">
                    <Header />
                    <div className="flex-1 overflow-y-auto p-6">
                        <Outlet />
                    </div>
                </main>
            </div>
        </ThemeProvider>
    );
};

export default Layout;
