// src/components/layout/ThemeMenu.jsx
import React, { useEffect, useRef, useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Palette } from "lucide-react";
import { useTheme } from "../../contexts/ThemeContext";

const ThemeMenu = () => {
  const { theme, setTheme, palettes } = useTheme();
  const [open, setOpen] = useState(false);
  const ref = useRef(null);

  // Cerrar al hacer click fuera
  useEffect(() => {
    const onDown = (e) => {
      if (ref.current && !ref.current.contains(e.target)) setOpen(false);
    };
    if (open) document.addEventListener("mousedown", onDown);
    return () => document.removeEventListener("mousedown", onDown);
  }, [open]);

  return (
    <div className="relative" ref={ref}>
      <motion.button
        className="p-2 rounded-lg hover:bg-gray-100 transition-colors"
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
        title="Cambiar tema"
        aria-haspopup="menu"
        aria-expanded={open}
        onClick={() => setOpen((o) => !o)}
      >
        <Palette className="w-6 h-6 text-gray-600" />
      </motion.button>

      <AnimatePresence>
        {open && (
          <motion.div
            role="menu"
            aria-label="Selector de tema"
            initial={{ opacity: 0, y: 6 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 6 }}
            transition={{ duration: 0.15 }}
            className="absolute right-0 mt-2 w-64 rounded-xl border border-[var(--border)] bg-[var(--surface)] shadow-lg p-3 z-50"
          >
            <p className="text-xs mb-2 text-[var(--muted)]">Paletas</p>
            <div className="grid grid-cols-3 gap-2">
              {palettes.map((p) => {
                const active = p.id === theme;
                return (
                  <button
                    key={p.id}
                    onClick={() => {
                      setTheme(p.id);
                      setOpen(false);
                    }}
                    className={`flex items-center gap-2 p-2 rounded-lg border transition-all text-[var(--text)] ${
                      active
                        ? "border-[var(--primary)] ring-2 ring-[var(--primary)]/30"
                        : "border-[var(--border)] hover:bg-gray-50"
                    }`}
                    title={p.label}
                    role="menuitem"
                  >
                    <span
                      className="inline-block w-6 h-6 rounded-full"
                      style={{
                        background: `linear-gradient(135deg, ${p.primary} 0%, ${p.secondary} 100%)`,
                      }}
                      aria-hidden="true"
                    />
                    <span className="text-sm">{p.label}</span>
                  </button>
                );
              })}
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default ThemeMenu;
