import React from 'react';
import { motion } from 'framer-motion';
import { LayoutDashboard, Users, Settings, Book, FileText, LogOut } from 'lucide-react';
import { NavLink, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import ThemeToggle from './ThemeToggle'; // Importar ThemeToggle

const Sidebar = ({ collapsed, toggleCollapse }) => {
  const location = useLocation();
  const navigate = useNavigate();
  const { logout } = useAuth();

  const menuItems = [
    { path: '/dashboard', label: 'Dashboard', icon: LayoutDashboard, permissions: ['dashboard'] },
    { path: '/security', label: 'Seguridad', icon: Users, permissions: ['security'] },
    { path: '/permissions', label: 'Permisos', icon: Settings, permissions: ['configuration'] },
    { path: '/liturgical', label: 'Litúrgico', icon: Book, permissions: ['liturgical'] },
    { path: '/reports', label: 'Reportes', icon: FileText, permissions: ['reports'] }
  ];

  // ... (resto del código del componente Sidebar) ...

  const user = {
    permissions: ['dashboard', 'security', 'configuration', 'liturgical', 'reports']
  }

  return (
    <motion.aside
      className={`bg-white border-r border-gray-200 w-64 flex flex-col pt-8 ${
        collapsed ? 'w-16' : 'w-64'
      } transition-width duration-300 overflow-hidden dark:bg-gray-800 dark:border-gray-700`} // Añadido estilos dark mode
    >
      <div className="px-4 pb-4">
        <img src="/logo.png" alt="Logo" className="w-auto h-8" />
      </div>

      <nav className="flex-1 px-2 space-y-1">
        {menuItems.map(
          (item) =>
            item.permissions.every((permission) => user?.permissions?.includes(permission)) && (
              <NavLink
                key={item.path}
                to={item.path}
                className={({ isActive }) =>
                  `flex items-center px-4 py-3 rounded-xl transition-colors hover:bg-gray-100 dark:hover:bg-gray-700 ${
                    isActive ? 'bg-blue-50 text-blue-600 font-medium' : 'text-gray-700 dark:text-gray-300'
                  }`
                }
              >
                <item.icon className="w-5 h-5 mr-3" />
                <span>{item.label}</span>
              </NavLink>
            )
        )}
      </nav>

      {/* Componente para cambiar el tema */}
      <div className="px-2 py-4">
        <ThemeToggle />
      </div>

      <div className="px-2 py-4">
        <button
          onClick={logout}
          className="flex items-center px-4 py-3 rounded-xl transition-colors hover:bg-red-50 text-red-600"
        >
          <LogOut className="w-5 h-5 mr-3" />
          <span>Cerrar Sesión</span>
        </button>
      </div>
    </motion.aside>
  );
};

export default Sidebar;
