import React, { useState } from 'react';
import { Outlet } from 'react-router-dom';
import Sidebar from './Sidebar';
import Header from './Header';
import { ThemeProvider } from '../contexts/ThemeContext'; // Importar ThemeProvider

const Layout = () => {
  const [collapsed, setCollapsed] = useState(false);

  const toggleSidebar = () => setCollapsed(!collapsed);

  return (
    <ThemeProvider> {/* Envolver la aplicación con ThemeProvider */}
      <div className="flex h-screen bg-gray-50 dark:bg-gray-900 dark:text-gray-200 transition-colors"> {/* Añadidas clases para dark mode */}
        <Sidebar collapsed={collapsed} toggleCollapse={toggleSidebar} />
        <main className="flex-1 flex flex-col overflow-hidden">
          <Header />
          <div className="flex-1 overflow-y-auto p-6">
            <Outlet />
          </div>
        </main>
      </div>
    </ThemeProvider>
  );
};

export default Layout;
