// src/components/Layout/Sidebar.js
import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import {
  Users, Calendar, DollarSign, ShoppingCart,
  Package, Settings, BarChart3, Shield, Church,
  FileText, Clock, TrendingUp, Archive
} from 'lucide-react';
import { useAuth } from '../../contexts/AuthContext';

const Sidebar = ({ collapsed }) => {
  const location = useLocation();
  const { user, hasPermission } = useAuth();

  // Lista de menús con permisos y rutas
  const menuItems = [
    { id: 'personal', title: 'Personal', icon: Users, path: '/personal', permission: 'personal' },
    {
      id: 'liturgical',
      title: 'Actos Litúrgicos',
      icon: Church,
      permission: 'liturgical',
      children: [
        { title: 'Gestionar Actos', path: '/liturgical/manage', icon: Church },
        { title: 'Horarios', path: '/liturgical/schedules', icon: Clock },
        { title: 'Reservas', path: '/liturgical/reservations', icon: Calendar },
        { title: 'Reportes', path: '/liturgical/reports', icon: FileText }
      ]
    },
    { id: 'sales', title: 'Ventas', icon: TrendingUp, path: '/sales', permission: 'sales' },
    { id: 'purchases', title: 'Compras', icon: ShoppingCart, path: '/purchases', permission: 'purchases' },
    { id: 'warehouse', title: 'Almacén', icon: Package, path: '/warehouse', permission: 'warehouse' },
    { id: 'accounting', title: 'Contabilidad', icon: DollarSign, path: '/accounting', permission: 'accounting' },
    {
      id: 'reports',
      title: 'Reportes',
      icon: BarChart3,
      permission: 'reports',
      children: [
        { title: 'Gerenciales', path: '/reports/management', icon: TrendingUp },
        { title: 'Transaccionales', path: '/reports/transactions', icon: Archive }
      ]
    },
    {
      id: 'security',
      title: 'Seguridad',
      icon: Shield,
      permission: 'security',
      children: [
        { title: 'Usuarios', path: '/security/users', icon: Users },
        { title: 'Roles', path: '/security/roles', icon: Shield },
        { title: 'Permisos', path: '/security/permissions', icon: Settings }
      ]
    },
    { id: 'configuration', title: 'Configuración', icon: Settings, path: '/configuration', permission: 'configuration' }
  ];

  // Filtra menús según permisos
  const filteredMenuItems = menuItems.filter(item => hasPermission(item.permission));

  // Activo si coincide la ruta
  const isActive = (path) => location.pathname.startsWith(path);

  return (
    <aside
      className="h-full flex flex-col shrink-0"
      style={{ 
        width: collapsed ? 80 : 256,
        background: "var(--surface)",
        borderRight: `1px solid var(--border)`
      }}
    >
      {/* Logo */}
      <Link
        to="/bienvenida"
        className={`flex items-center gap-3 px-4 py-4 border-b ${collapsed ? 'justify-center' : ''}`}
        style={{ borderColor: "var(--border)" }}
      >
        <Church className="w-8 h-8" style={{ color: "var(--primary)" }} />
        {!collapsed && (
          <div>
            <h1 className="text-lg font-bold" style={{ color: "var(--text)" }}>SisParroquia</h1>
            <p className="text-xs" style={{ color: "var(--muted)" }}>Sistema de Gestión</p>
          </div>
        )}
      </Link>

      {/* Menú directo (solo submenús u opciones finales) */}
      <nav className="flex-1 overflow-y-auto p-2 space-y-1">
        {filteredMenuItems.flatMap(item => {
          if (item.children) {
            // Renderiza directamente los submenús
            return item.children.map(child => (
              <Link
                key={child.path}
                to={child.path}
                className={`flex items-center p-2 rounded-lg text-sm ${collapsed ? 'justify-center' : 'gap-3'}`}
                style={{
                  background: isActive(child.path) ? "var(--surface-2)" : "transparent",
                  color: isActive(child.path) ? "var(--primary)" : "var(--text)"
                }}
              >
                <child.icon className="w-4 h-4" />
                {!collapsed && <span>{child.title}</span>}
              </Link>
            ));
          } else {
            // Renderiza el ítem normal
            return (
              <Link
                key={item.id}
                to={item.path}
                className={`flex items-center p-2 rounded-lg text-sm ${collapsed ? 'justify-center' : 'gap-3'}`}
                style={{
                  background: isActive(item.path) ? "var(--surface-2)" : "transparent",
                  color: isActive(item.path) ? "var(--primary)" : "var(--text)"
                }}
              >
                <item.icon className="w-4 h-4" />
                {!collapsed && <span>{item.title}</span>}
              </Link>
            );
          }
        })}
      </nav>

      {/* Información usuario */}
      <div className="p-2 border-t" style={{ borderColor: "var(--border)" }}>
        <div className={`flex items-center gap-2 ${collapsed ? 'justify-center' : ''}`}>
          <div 
            className="w-8 h-8 rounded-full flex items-center justify-center"
            style={{
              background: "linear-gradient(135deg, var(--primary), var(--secondary))"
            }}
          >
            <span className="text-white text-sm font-bold">{user?.name?.charAt(0) || 'U'}</span>
          </div>
          {!collapsed && (
            <div>
              <p className="text-sm font-medium truncate" style={{ color: "var(--text)" }}>{user?.name || 'Usuario'}</p>
              <p className="text-xs capitalize" style={{ color: "var(--muted)" }}>{user?.role || 'usuario'}</p>
            </div>
          )}
        </div>
      </div>
    </aside>
  );
};

export default Sidebar;
